// import { baseUrl } from '../app/shared/baseUrl';

export const mapImageURL = (arr) => {
    return arr.map((item) => {
        console.log("testing",item.image.split('/'));
        return {
            ...item,
            img: '../app/assets/img/' + item.image
        };
    });
};
