import { formatDate } from '../../utils/formatDate';

const Comment = ({ comment }) => {
    const {text: commentText, rating, author, date} = comment; // descructure comment object and rename text to commentText
    return (
        <p>
            {commentText} <br />
            {rating}/5 stars -- {author}, {formatDate(date)} 
        </p>
    )
};

export default Comment;
