// import { baseUrl } from '../app/shared/baseUrl';

export const mapImageURL = (arr) => {
    const baseUrl =  '../app/assets/';
    return arr.map((item) => {
        return {
            ...item,
            image: baseUrl+ item.image
        };
    });
};
